analyzer.EXIT_PARSING_FAILED = 251;
analyzer.EXIT_EMPTY_CSS = 252;
analyzer.EXIT_CSS_PASSED_IS_NOT_STRING = 253;
analyzer.EXIT_URL_LOADING_FAILED = 254;
analyzer.EXIT_FILE_LOADING_FAILED = 255;

